import React, { useEffect, useState } from "react";

export default function AdminDashboard() {
  const [submissions, setSubmissions] = useState([]);
  const API_BASE = process.env.REACT_APP_API_URL || "http://localhost:5001";

  useEffect(() => {
    const loadData = async () => {
      try {
        let data = [];

        // ‚úÖ 1. Load from localStorage
        const local = JSON.parse(localStorage.getItem("submissions") || "[]");
        data = [...local];

        // ‚úÖ 2. Load from backend if available
        if (API_BASE) {
          const res = await fetch(`${API_BASE}/api/partners`);
          if (res.ok) {
            const backendData = await res.json();
            data = [...data, ...backendData.data || backendData];
          }
        }

        setSubmissions(data);
      } catch (err) {
        console.error("Error fetching submissions:", err);
      }
    };

    loadData();
  }, [API_BASE]);

  // ‚úÖ Clear submissions from localStorage
  const handleClear = () => {
    if (window.confirm("Are you sure you want to clear all submissions?")) {
      localStorage.removeItem("submissions");
      setSubmissions([]); // reset state
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ fontWeight: "bold", marginBottom: "20px" }}>üìä Admin Dashboard</h2>

      <button
        onClick={handleClear}
        style={{
          marginBottom: "20px",
          padding: "10px 15px",
          background: "red",
          color: "white",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
        }}
      >
        üóëÔ∏è Clear All Submissions
      </button>

      {submissions.length === 0 ? (
        <p>No submissions yet.</p>
      ) : (
        submissions.map((s, idx) => (
          <div
            key={idx}
            style={{
              border: "1px solid #ddd",
              borderRadius: "8px",
              padding: "15px",
              marginBottom: "15px",
              background: "#f9f9f9",
            }}
          >
            <h3>{s.companyName}</h3>
            <p><b>Contact Person:</b> {s.contactPerson}</p>
            <p><b>Email:</b> {s.email}</p>
            <p><b>Phone:</b> {s.phone}</p>
            <p><b>Services:</b> {s.services}</p>
            <p><b>Location:</b> {s.location}</p>
          </div>
        ))
      )}
    </div>
  );
}
